void chord_assign_dynamics(t_notation_obj *r_ob, t_chord *chord, t_jfont *jf_dynamics_nozoom, t_jfont *jf_dynamics_roman_nozoom);
char chord_delete_dynamics(t_notation_obj *r_ob, t_chord *chord, char add_undo_tick);
